// AI Content Analyzer - Scores viral potential and relevance

const items = $input.all();
const scoredItems = [];

// Keywords for Adam James's niches
const nicheKeywords = {
  entrepreneurship: ['entrepreneur', 'startup', 'business', 'founder', 'small business', 'growth'],
  mentalHealth: ['mental health', 'therapy', 'wellness', 'support', 'counseling'],
  corporateHousing: ['remote work', 'housing', 'corporate housing', 'relocation', 'work from home'],
  tulsa: ['tulsa', 'oklahoma', 'small city', 'midwest'],
  ai: ['ai', 'artificial intelligence', 'automation', 'technology']
};

// Score each item
for (const item of items) {
  const title = (item.json.title || '').toLowerCase();
  const description = (item.json.description || '').toLowerCase();
  const content = title + ' ' + description;

  let score = 0;
  let matchedNiches = [];

  // Check for niche relevance
  for (const [niche, keywords] of Object.entries(nicheKeywords)) {
    for (const keyword of keywords) {
      if (content.includes(keyword)) {
        score += 10;
        if (!matchedNiches.includes(niche)) {
          matchedNiches.push(niche);
        }
      }
    }
  }

  // Bonus for multiple niche matches
  if (matchedNiches.length > 1) {
    score += 20;
  }

  // Bonus for Tulsa mentions
  if (content.includes('tulsa') || content.includes('oklahoma')) {
    score += 30;
  }

  // Bonus for actionable content
  if (content.includes('how to') || content.includes('tips') || content.includes('guide')) {
    score += 15;
  }

  // Store scored item
  if (score > 20) { // Only keep relevant items
    scoredItems.push({
      json: {
        ...item.json,
        viralityScore: score,
        matchedNiches: matchedNiches,
        relevanceReason: `Matches niches: ${matchedNiches.join(', ')}`
      }
    });
  }
}

// Sort by score and take top 3
const topItems = scoredItems
  .sort((a, b) => b.json.viralityScore - a.json.viralityScore)
  .slice(0, 3);

return topItems;

// Script Generator - Creates 30-second Reel scripts

