    hook = "Everyone told me I was crazy for ";
  } else if (niches.includes('mentalHealth')) {
    hook = "Here's the truth about mental health that nobody talks about ";
  } else if (niches.includes('corporateHousing')) {
    hook = "The future of work just changed everything ";
  }

  // Create full script
